function OnClose(self)	----os.exit 效果等同于windows的exit函数，不推荐实际应用中直接使用	os.exit()endfunction OnMainWndSize(self, type_, width, height)	local objectTree = self:GetBindUIObjectTree()	local rootObject = objectTree:GetRootObject()		rootObject:SetObjPos(0, 0, width, height)endfunction btnOK_OnLButtonDown(self)	XLMessageBox("btnOK_OnLButtonDown")endfunction btnCancel_OnLButtonDown(self)	XLMessageBox("btnCancel_OnLButtonDown")end