function OnClose(self)	----os.exit 效果等同于windows的exit函数，不推荐实际应用中直接使用	os.exit()endfunction OnMainWndSize(self, type_, width, height)	local objectTree = self:GetBindUIObjectTree()	local rootObject = objectTree:GetRootObject()		rootObject:SetObjPos(0, 0, width, height)endfunction OnMainWndFocusChange(bool focus)	local hostWndManager = XLGetObject("Xunlei.UIEngine.HostWndManager")	local mainWnd = hostWndManager:GetHostWnd("MainFrame")	local uiObjectTree = mainWnd:GetBindUIObjectTree()	local mainForm = self:GetControlObject("mainwnd.bkg1")	if focus then		mainForm.SetBkg("foxmail.bitmap.bkg")  else   mainForm.SetBkg("foxmail.bitmap.bkglost")   endifend