function OnClose(self)	----os.exit 效果等同于windows的exit函数，不推荐实际应用中直接使用	os.exit()endfunction OnMainWndSize(self, type_, width, height)	local objectTree = self:GetBindUIObjectTree()	local rootObject = objectTree:GetRootObject()		rootObject:SetObjPos(0, 0, width, height)endfunction OnUserRadioButtonClick(self)	self:SetSelect(not self:GetSelect())endfunction OnRadioButtonContainerInit(self)	self:AddRadioButton("btn1","分组单选1",0,0,100,24)	self:AddRadioButton("btn2","分组单选2",0,28,100,24)	self:AddRadioButton("btn3","分组单选3",0,56,100,24)end